import React, { useRef, useState } from "react";
import { Canvas, useFrame } from "react-three-fiber";
import { Box, Cone, softShadows, MeshWobbleMaterial, OrbitControls } from "drei";
import { Header } from "./components/ui";
import "./App.scss";
import { useSpring, a } from "react-spring/three";
import { Lights, Tree, Floor } from './components/scene';

softShadows();

const App = () => {
  return (
    <>
      <Header />
      <Canvas
        colorManagement
        shadowMap
        camera={{ position: [-7, 10, 3], fov: 60 }}
        background={'pink'}>
        <Lights />
        <group>
          <Floor />
          <Tree
            position={[0, 2.5, 0]}
            color='#669966'
            args={[3, 9, 90]}
            speed={2}
          />
        </group>

        <OrbitControls 
          enableZoom={false}
        />
      </Canvas>
    </>
  );
};

export default App;
