import React, { useRef, useState, useEffect } from "react";
import { Canvas } from "react-three-fiber";
import { softShadows, OrbitControls } from "drei";
import { Header } from "./components/ui";
import "./App.scss";
import { Lights, Tree, Floor, Ball } from './components/scene';

softShadows();

const App = () => {
  const [spheres, setSpheres] = useState([]);

  const newElement = <Ball position={[0, 5, 0]} color='red' args={[.2, 10, 10]}/>;

  useEffect(() => {
    setSpheres([...spheres, newElement]);
  }, []);

  return (
    <>
      <Header />
      <Canvas
        colorManagement
        shadowMap
        camera={{ position: [-7, 10, 3], fov: 60 }}
        background={'pink'}>
        <Lights />

          <group>
          <Floor />

          <Tree
            spheres={spheres}
            position={[0, 0, 0]}
            color='#669966'
            args={[3, 9, 9]}
            speed={2}
          />

          {/* <Ball
            position={[0, 5, 0]}
            color='red'
            args={[.2, 10, 10]}
          /> */}
          {spheres.map(sphere => { 
            return sphere
          })}

        </group>
        <OrbitControls 
          // enableZoom={false}
        />
      </Canvas>
    </>
  );
};

export default App;
