import React, { useRef, useState, useEffect } from "react";
import { useSpring, a } from "react-spring/three";
import { useFrame } from "react-three-fiber";
import { Ball } from '../scene';

const Tree = ({ setSpheres, position, color, speed, args }) => {


    // useEffect(() => {
    //     setSpheres([newElement]);
    // }, []);



    const mesh = useRef();
    // useFrame(() => (mesh.current.rotation.x = mesh.current.rotation.y += 0.01));

    const clickedOnTree = (e) => {
        console.log(e.point);
        console.log(e.point.x);
        const newElement = <Ball position={[e.point.x, e.point.y, (e.point.z + 2)]} color='red' args={[.2, 10, 10]}/>;
        setSpheres([newElement]);
    }

    const [expand, setExpand] = useState(false);
    const props = useSpring({
        scale: expand ? [1.4, 1.4, 1.4] : [1, 1, 1],
    });

    return (
        <a.mesh
        onPointerDown={e => clickedOnTree(e)}

        position={position}
        ref={mesh}

        // onClick={() => setExpand(!expand)}

        scale={props.scale}
        castShadow>
            <coneBufferGeometry attach='geometry' args={args} />
            <meshStandardMaterial attach='material' color={color} />
        </a.mesh>
    );

}

export default Tree;
