import React, { useRef, useState } from "react";

const Bauble = ({ position, color, speed, args }) => {

    return (
        <mesh
        opacity={0.2}
        transparent={true}
        position={position}
        castShadow>
            <sphereBufferGeometry attach='geometry' args={args} />
            <meshStandardMaterial attach='material' color={color} />
        </mesh>
    );

}

export default Bauble;
